#molecula framework
from rdkit import Chem
from rdkit.Chem import Draw


from flask import Flask, render_template
app = Flask(__name__)

bb=''

@app.route("/drawMolecule")
def drawMolecule():
    user = "Aqui deveria haver a molecula"
    image = molecula()
    return render_template('index.html', image = bb)

# Todo: Convert image to base64
def molecula():
    x = Chem.MolFromSmiles('CCOCCNSC=O')
    image = Draw.MolToImage(x)
    return image.tobytes()
